type Sequence = {
  tracks: Track[];
};

type Track = {
   id: string;                 // stable id (recommended)
  channelVolume: number;
  channelEnabled: boolean;
  channelName: string;
  channelEditing?: boolean;
  pattern: Pattern;
  url?: string;  // local file URL (for loading samples)
  triggered?: boolean;        // 👈 add this (default false)
};

type Pattern = {
  measures: Measure[];
};

type Measure = {
  beats: Beat[];
};

type Beat = {
  steps: Step[];
};

type Step = {
  active: boolean;
  triggered: boolean;
};



// ------ Audio plumbing (add near the top-level of the component file) ------
type TrackVoices = {
  pool: Tone.Player[];
  next: number;
  channel: Tone.Channel;   // for per-track volume/mute/etc
};

const VOICES_PER_TRACK = 4; // small pool is plenty for drums  

// Helper: derive a lightweight "plan" that only changes when the grid shape changes.
// If you want even less sensitivity, make this only include counts, not actives.
function shapeSignature(seq: Sequence) {
  // counts only (measures / beats per measure / steps per beat)
  const measures = seq.tracks?.[0]?.pattern?.measures ?? [];
  const perMeasure = measures.map(m => m.beats.length + ":" + m.beats[0]?.steps.length);
  return `${measures.length}|${perMeasure.join(",")}`;
}

// ---- unchanged helpers from earlier, with volume+mute applied ----
function createTrackVoices(url: string, volume0to10 = 10, enabled = true): TrackVoices {
  // convert 0..10 → linear 0..1 (then → dB); clamp to avoid -Infinity
  const lin = Math.max(0.0001, volume0to10 / 10);
  const channel = new Tone.Channel({
    volume: Tone.gainToDb(lin),
    mute: !enabled,
  }).toDestination();

  const pool: Tone.Player[] = [];
  for (let i = 0; i < VOICES_PER_TRACK; i++) {
    pool.push(
      new Tone.Player({ url, autostart: false, fadeOut: 0.005 }).connect(channel)
    );
  }
  return { pool, next: 0, channel };
}

function disposeTrackVoices(v: TrackVoices) {
  v.pool.forEach(p => { try { p.stop(); } catch {} p.dispose(); });
}

function triggerTrack(voices: TrackVoices, time: number) {
  const v = voices.pool[voices.next];
  voices.next = (voices.next + 1) % voices.pool.length;
  // Using 'time' from the Transport callback gives sample-accurate hits
  v.start(time);
}

