const kickUrl = "data:audio/mpeg;base64,";
const snareUrl = "data:audio/mpeg;base64,";


function App() {

  function makeEmptyPattern(
    measures = 1,
    beatsPerMeasure = 4,
    stepsPerBeat = 4
  ): Pattern {
    return {
      measures: Array.from({ length: measures }, () => ({
        beats: Array.from({ length: beatsPerMeasure }, () => ({
          steps: Array.from({ length: stepsPerBeat }, () => ({
            active: false,
            triggered: false,
          })),
        })),
      })),
    };
  }

  function mkTrack(name: string, sampleUrl: string): Track {
    const safeId =
      typeof crypto?.randomUUID === "function"
        ? crypto.randomUUID()
        : Math.random().toString(36).slice(2); // fallback for older browsers

    return {
      id: safeId,
      channelName: name,
      channelVolume: 10,
      channelEnabled: true,
      url: sampleUrl,
      triggered: false,
      pattern: makeEmptyPattern(), // matches your default grid
    };
  }

  const [sequence, setSequence] = useState<Sequence>(() => ({
    tracks: [
      mkTrack("KICK", kickUrl),
      mkTrack("SNARE", snareUrl),
    ],
  }));

  const [audioReady, setAudioReady] = useState(false);

  const modal = useModal();

  // Keep the latest sequence in a ref so the loop callback always sees current data,
  // without making the effect depend on the whole object.
  const sequenceRef = useRef(sequence);

  // track.id -> timeout handle
  const triggerTimersRef = useRef<Record<string, number>>({});
  useEffect(() => {
    // cleanup on unmount
    Object.values(triggerTimersRef.current).forEach(clearTimeout);
  }, []);

    // choose a constant flash window, or derive from step length
  const flashMs = 120; // or Math.max(60, Math.min(200, stepSeconds*1000*0.8))

  const flashTrack = useCallback((trackId: string) => {
    // set true
    setSequence(prev => {
      const tracks = prev.tracks.map(t =>
        t.id === trackId ? { ...t, triggered: true } : t
      );
      return { ...prev, tracks };
    });

    // refresh/replace any existing clear timer
    const existing = triggerTimersRef.current[trackId];
    if (existing) clearTimeout(existing);

    triggerTimersRef.current[trackId] = window.setTimeout(() => {
      setSequence(prev => {
        const tracks = prev.tracks.map(t =>
          t.id === trackId ? { ...t, triggered: false } : t
        );
        return { ...prev, tracks };
      });
      delete triggerTimersRef.current[trackId];
    }, flashMs);
  }, [setSequence]);


  const getTrackSampleUrl = (trackIndex: number) =>
    sequence.tracks[trackIndex]?.url ?? "";

  // In your component:
  const instrumentsRef = useRef<Record<string, TrackVoices>>({});

  // --- transport/time signature ---
  const [bpm, setBpm] = useState(120);
  const [isPlaying, setIsPlaying] = useState(false);

  const [measureCountInput, setMeasureCountInput] = useState(
    sequence?.tracks[0]?.pattern.measures.length ?? 2
  );

  const tracksShapeKey = useMemo(
    () => sequence.tracks.map((t) => t.url).join("|"),
    [sequence.tracks]
  );

  useEffect(() => {
    const seq = sequenceRef.current;

    // Dispose old
    Object.values(instrumentsRef.current).forEach(v => {
      v.pool.forEach(p => p.dispose());
      v.channel.dispose();
    });
    instrumentsRef.current = {};

    // Build per-track voices
    seq.tracks.forEach((track, idx) => {
      const url = getTrackSampleUrl(idx);
      instrumentsRef.current[idx] = createTrackVoices(
        url, 
        Tone.gainToDb(track.channelVolume / 10),
        track.channelEnabled
      );
    });
  }, [tracksShapeKey]);
  
  // Beat length: '4n' for denom=4, '8n' for denom=8, etc.
  function beatSeconds(den: 1|2|4|8|16) {
    return Tone.Time(`${den}n`).toSeconds();
  }

  // e.g., 4/4, 3/4, 6/8, etc.
  const [timeSig, setTimeSig] = useState<{ numerator: number; denominator: 1|2|4|8|16 }>({
    numerator: 4,
    denominator: 4,
  });

  // steps per beat (subdivision). 4 = sixteenth notes for quarter-note beats.
  // Keep this simple for now; you can add a UI to switch (2=8ths, 3=triplets, etc.)
  const [subdivisionsPerBeat, setSubdivisionsPerBeat] = useState(4);
  const loopRef = useRef<Tone.Loop | null>(null);

  const [swing, setSwing] = useState(0.0); // 0..1

  // --- playhead ---
  type Idx = { measure: number; beat: number; step: number, paused?: boolean };
  const playheadRef = useRef<Idx>({ measure: 0, beat: 0, step: 0 });

  // utils
  const stepNotation = useMemo(
    () => `${timeSig.denominator * subdivisionsPerBeat}n`,
    [timeSig.denominator, subdivisionsPerBeat]
  );


  useEffect(() => {
    const seq = sequenceRef.current;
    const idsInState = new Set(seq.tracks.map(t => t.id));

    console.log('idsInState', idsInState);

    // dispose removed
    Object.entries(instrumentsRef.current).forEach(([id, v]) => {
      console.log('checking', id);
      if (!idsInState.has(id)) {
        console.log('disposing', id);
        disposeTrackVoices(v);
        delete instrumentsRef.current[id];
      }
    });

    // create new
    seq.tracks.forEach((t) => {
      if (!instrumentsRef.current[t.id]) {
        const url = t.url ?? (t.channelName === "KICK" ? "/samples/kick.wav" : "/samples/hat.wav");
        instrumentsRef.current[t.id] = createTrackVoices(url, t.channelVolume, t.channelEnabled);
      }
    });
  }, [sequence.tracks]);

  useEffect(() => {
    sequenceRef.current = sequence;
  }, [sequence]);

  useEffect(() => {
    sequenceRef.current.tracks.forEach((t, i) => {
      const voices = instrumentsRef.current[i];
      if (!voices) return;

      // mute follows channelEnabled
      voices.channel.mute = !t.channelEnabled;

      // volume follows channelVolume (0..10)
      const lin = Math.max(0.0001, t.channelVolume / 10);
      voices.channel.volume.value = Tone.gainToDb(lin);
    });
  }, [sequenceRef.current.tracks]); // runs whenever volume/mute in state change  

  // If you need to react to *shape* changes, compute a stable signature.
  // If you *don't* need this, you can delete shapeSig and remove it from deps below.
  const shapeSig = useMemo(() => shapeSignature(sequence), [sequence]);
    
  // // Also keep latest "playerLoaded" in a ref so the loop callback can gate playback.
  // const playerLoadedRef = useRef(playerLoaded);
  // useEffect(() => {
  //   playerLoadedRef.current = playerLoaded;
  // }, [playerLoaded]);

  useEffect(() => {
    const transport = Tone.getTransport();
    transport.swing = swing; // 0..1
    transport.swingSubdivision = stepNotation as Subdivision;
  }, [swing]);

  // One step = one beat / subdivisionsPerBeat
  const stepSeconds = useMemo(() => {
    return beatSeconds(timeSig.denominator) / subdivisionsPerBeat;
  }, [timeSig.denominator, subdivisionsPerBeat]);

  useEffect(() => {
    const transport = Tone.getTransport();
    transport.bpm.value = bpm;
    transport.timeSignature = [timeSig.numerator, timeSig.denominator];
  }, [bpm, timeSig.numerator, timeSig.denominator]);


  useEffect(() => {

    loopRef.current?.dispose();

    if(!loopRef.current) {

      const loop = new Tone.Loop((time) => {
        const seq = sequenceRef.current; // <-- latest sequence, no re-subscribe
        const cur = playheadRef.current;
        
        const next = getNextIndex(seq, cur);
        const prev = getPrevIndex(seq, cur);

        setTriggeredAt(prev, cur);
        playheadRef.current = next;

        // Play using latest seq + gated by latest playerLoaded
        seq.tracks.forEach((track, trackIdx) => {
          const step = track.pattern.measures[cur.measure].beats[cur.beat].steps[cur.step];
          if (step.active && track.channelEnabled) {
            triggerTrack(instrumentsRef.current[track.id], time);
            flashTrack(track.id);         // 👈 state-only flash
          }
        });

      }, stepNotation);

      loopRef.current = loop;
    }

    if (isPlaying) loopRef.current?.start(0);

    return () => {
      loopRef.current?.dispose();
      loopRef.current = null;
    };

    // Include playerLoaded/samplerLoaded so gating updates immediately
  }, [isPlaying, shapeSig, stepNotation]);


  useEffect(() => {
    if (!isPlaying) {
      setSequence(prev => ({ ...prev, tracks: prev.tracks.map(t => ({ ...t, triggered: false })) }));
      Object.values(triggerTimersRef.current).forEach(clearTimeout);
      triggerTimersRef.current = {};
    }
  }, [isPlaying, setSequence]);


  const removeTrack = (trackId: string) => {
    // 1) dispose audio immediately so nothing rings out
    const voices = instrumentsRef.current[trackId];
    if (voices) {
      disposeTrackVoices(voices);
      delete instrumentsRef.current[trackId];
    }

    // 2) remove from React state
    setSequence(prev => ({ ...prev, tracks: prev.tracks.filter(t => t.id !== trackId) }));
  };


  const onChangeMeasures = (n: number) => {
    // choose sensible bounds; adjust as you like
    const clamped = Math.max(1, Math.min(128, n));
    setMeasureCountInput(clamped);
    setMeasureCount(clamped);
  };

  // --- build a fresh pattern per time signature ---
  function buildEmptyPattern(measureCount: number, beatsPerMeasure: number, stepsPerBeat: number): Pattern {
    const blankStep = (): Step => ({ active: false, triggered: false });
    const beat = (): Beat => ({ steps: Array.from({ length: stepsPerBeat }, blankStep) });
    const measure = (): Measure => ({ beats: Array.from({ length: beatsPerMeasure }, beat) });
    return { measures: Array.from({ length: measureCount }, measure) };
  }

  // Rebuild every track's grid when time-signature/subdivision changes
  function rebuildGrid(numerator: number, stepsPerBeat: number) {
    setSequence(prev => {
      if (!prev) return prev;
      return {
        ...prev,
        tracks: prev.tracks.map(t => ({
          ...t,
          pattern: buildEmptyPattern(
            t.pattern.measures.length,   // keep same measure count
            numerator,                   // beats per measure
            stepsPerBeat                 // steps per beat
          )
        }))
      };
    });
    // reset playhead and clear any “triggered” artifacts conceptually
    playheadRef.current = { measure: 0, beat: 0, step: 0 };
  }

  // Call this whenever the user changes time signature
  function setTimeSignature(numerator: number, denominator: 1|2|4|8|16) {
    setTimeSig({ numerator, denominator });
    rebuildGrid(numerator, subdivisionsPerBeat);
  }

  // Optional: call if you expose a UI for changing subdivisions
  function setSubdivision(stepsPerBeat: number) {
    setSubdivisionsPerBeat(stepsPerBeat);
    rebuildGrid(timeSig.numerator, stepsPerBeat);
  }



  // Helpers to immutably update nested sequence state
  function setTrackProp(
    ti: number,
    updater: (t: Track) => Track
  ) {
    setSequence(prev => {
      if (!prev) return prev;
      return {
        ...prev,
        tracks: prev.tracks.map((t, i) => (i === ti ? updater(t) : t)),
      };
    });
  }

  function toggleStep(ti: number, mi: number, bi: number, si: number) {
    setSequence(prev => {
      if (!prev) return prev;
      return {
        ...prev,
        tracks: prev.tracks.map((t, tIdx) =>
          tIdx !== ti
            ? t
            : {
                ...t,
                pattern: {
                  ...t.pattern,
                  measures: t.pattern.measures.map((m, mIdx) =>
                    mIdx !== mi
                      ? m
                      : {
                          ...m,
                          beats: m.beats.map((b, bIdx) =>
                            bIdx !== bi
                              ? b
                              : {
                                  ...b,
                                  steps: b.steps.map((s, sIdx) =>
                                    sIdx !== si ? s : { ...s, active: !s.active }
                                  ),
                                }
                          ),
                        }
                  ),
                },
              }
        ),
      };
    });
  }

  // Channel editing toggles
  function setChannelEditing(ti: number, editing: boolean) {
    setTrackProp(ti, t => ({ ...t, channelEditing: editing }));
  }
  function setChannelNameAt(ti: number, name: string) {
    console.log("setChannelNameAt", ti, name);
    setTrackProp(ti, t => ({ ...t, channelName: name }));
  }

  function getNextIndex(seq: Sequence, cur: Idx): Idx {
    const M = seq.tracks[0].pattern.measures.length;
    const measure = seq.tracks[0].pattern.measures[cur.measure];
    const B = measure.beats.length;
    const beat = measure.beats[cur.beat];
    const S = beat.steps.length;

    let m = cur.measure, b = cur.beat, s = cur.step + 1;
    if (s >= S) { s = 0; b += 1; }
    if (b >= B) { b = 0; m += 1; }
    if (m >= M) { m = 0; }
    return { measure: m, beat: b, step: s };
  }

  function getPrevIndex(seq: Sequence, cur: Idx): Idx {
    const M = seq.tracks[0].pattern.measures.length;

    let m = cur.measure, b = cur.beat, s = cur.step - 1;
    if (s < 0) {
      b -= 1;
      if (b < 0) {
        m -= 1;
        if (m < 0) m = M - 1;
        b = seq.tracks[0].pattern.measures[m].beats.length - 1;
      }
      s = seq.tracks[0].pattern.measures[m].beats[b].steps.length - 1;
    }
    return { measure: m, beat: b, step: s };
  }

  // Flip only the two involved steps (previous off, current on), across all tracks
  function setTriggeredAt(prevIdx: Idx, curIdx: Idx) {
    setSequence(prev => {
      if (!prev) return prev;
      return {
        ...prev,
        tracks: prev.tracks.map(t => ({
          ...t,
          pattern: {
            ...t.pattern,
            measures: t.pattern.measures.map((m, mi) => {
              if (mi !== prevIdx.measure && mi !== curIdx.measure) return m;
              return {
                ...m,
                beats: m.beats.map((b, bi) => {
                  const touchingPrev = mi === prevIdx.measure && bi === prevIdx.beat;
                  const touchingCur  = mi === curIdx.measure  && bi === curIdx.beat;
                  if (!touchingPrev && !touchingCur) return b;
                  return {
                    ...b,
                    steps: b.steps.map((s, si) => {
                      const isPrev = touchingPrev && si === prevIdx.step;
                      const isCur  = touchingCur  && si === curIdx.step;
                      if (isPrev) return { ...s, triggered: false };
                      if (isCur)  return { ...s, triggered: true  };
                      return s;
                    })
                  };
                })
              };
            })
          }
        }))
      };
    });
  }

  const makeBlankStep = (): Step => ({ active: false, triggered: false });

  const makeBeat = (stepsPerBeat: number): Beat => ({
    steps: Array.from({ length: stepsPerBeat }, makeBlankStep),
  });

  const makeMeasure = (beatsPerMeasure: number, stepsPerBeat: number): Measure => ({
    beats: Array.from({ length: beatsPerMeasure }, () => makeBeat(stepsPerBeat)),
  });

  function clearAllTriggered() {
    setSequence(prev => {
      if (!prev) return prev;
      return {
        ...prev,
        tracks: prev.tracks.map(t => ({
          ...t,
          pattern: {
            ...t.pattern,
            measures: t.pattern.measures.map(m => ({
              ...m,
              beats: m.beats.map(b => ({
                ...b,
                steps: b.steps.map(s => ({ ...s, triggered: false })),
              })),
            })),
          },
        })),
      };
    });
  }

  function setMeasureCount(newCount: number) {
    const beatsPerMeasure = timeSig.numerator;        // e.g., 3 in 3/4, 6 in 6/8
    const stepsPerBeat = subdivisionsPerBeat;         // e.g., 4 for 16ths

    // 1) reshape measures across all tracks
    setSequence(prev => {
      if (!prev) return prev;

      return {
        ...prev,
        tracks: prev.tracks.map(t => {
          const current = t.pattern.measures;
          let measures: Measure[];

          if (newCount <= current.length) {
            // truncate
            measures = current.slice(0, newCount);
          } else {
            // append
            const toAdd = newCount - current.length;
            const extra = Array.from({ length: toAdd }, () =>
              makeMeasure(beatsPerMeasure, stepsPerBeat)
            );
            measures = [...current, ...extra];
          }

          return {
            ...t,
            pattern: { ...t.pattern, measures },
          };
        }),
      };
    });

    // 2) sanitize playhead + clear triggers
    // (Keep playhead if still valid; otherwise clamp to the new last cell)
    const maxM = Math.max(0, newCount - 1);
    const maxB = Math.max(0, beatsPerMeasure - 1);
    const maxS = Math.max(0, stepsPerBeat - 1);

    const p = playheadRef.current;
    const clamped: typeof p = {
      measure: Math.min(p.measure, maxM),
      beat: Math.min(p.beat, maxB),
      step: Math.min(p.step, maxS),
    };
    playheadRef.current = clamped;

    clearAllTriggered();
  }

  // update volume (0..10) for a track
  const setTrackVolume = (trackIdx: number, vol0to10: number) => {
    setSequence(prev => {
      const tracks = [...prev.tracks];
      tracks[trackIdx] = { ...tracks[trackIdx], channelVolume: Math.max(0, Math.min(10, vol0to10)) };
      return { ...prev, tracks };
    });
  };

  // toggle mute (channelEnabled true/false)
  const setTrackEnabled = (trackIdx: number, enabled: boolean) => {
    setSequence(prev => {
      const tracks = [...prev.tracks];
      tracks[trackIdx] = { ...tracks[trackIdx], channelEnabled: enabled };
      return { ...prev, tracks };
    });
  };

  const onStop = () => {
    setIsPlaying(false);
    playheadRef.current = { measure: 0, beat: 0, step: 0 };
    setSequence(prev => {
      if (!prev) return prev;
      return {
        ...prev,
        tracks: prev.tracks.map(t => ({
          ...t,
          pattern: {
            ...t.pattern,
            measures: t.pattern.measures.map(m => ({
              ...m,
              beats: m.beats.map(b => ({
                ...b,
                steps: b.steps.map(s => ({ ...s, triggered: false }))
              }))
            }))
          }
        }))
      };
    });
  }

  const addTrack = (name: string, sampleUrl: string) => {
    const newTrack = mkTrack(name, sampleUrl);
    setSequence(prev => ({ ...prev, tracks: [...prev.tracks, newTrack] }));
  };

  const showAddTrack = () => {
    modal.open(MyModal, { props: {title: "hello" }})
  };

  if(!audioReady) {
    return (
    <Container>
      <Typography>Please click the button below to start the audio engine</Typography>
      <Button variant='contained' onClick={async () =>{ await Tone.start(); setAudioReady(true); } }>Start Audio</Button>
    </Container>
    );
  }

  return (
    <>

    <MainSection>
      <header style={{
        backgroundColor: "#000615", 
        padding: "15px",
        marginBottom: "30px",
        boxShadow: "0 1px 1px rgba(0, 0, 0, .8), 0 -1px rgba(195, 201, 217, .075) inset"}}>
        <Typography style={{
          fontSize: "175%",
          color: "#AAB8D9",
          fontWeight: "normal", 
          lineHeight: "1",
          textShadow: "1px 1px 2px rgba(255, 255, 255, .15), 0 0 1px black"}}>Step sequencer</Typography>
      </header>
      <SequencerSection>
        <TransportSection>
          <TransportButton
            onClick={async () => {
                // await Tone.start(); // resume audio context on first gesture

                setIsPlaying(!isPlaying);
                playheadRef.current = { ...playheadRef.current, paused: isPlaying };

                if(isPlaying) {
                  Tone.Transport.pause();
                } else {
                  if (Tone.Transport.state !== "started") {
                    Tone.Transport.start();
                  }
                  loopRef.current?.start(0);
                }
              }}
            >

              {isPlaying ? <PauseIcon sx={{color: "#eee", padding: "4px", height: 42, width: 42}} /> : <PlayArrowIcon sx={{color: "#eee", padding: "4px", height: 42, width: 42}} />}
          </TransportButton>
          <TransportButton
            onClick={onStop}>
              <StopIcon sx={{color: "#eee", padding: "4px", height: 42, width: 42}} />
          </TransportButton>
          <Box sx={{
            marginRight: "10px",
            marginLeft: "15px",
            display: "flex",
            flexDirection: "row",
            alignItems: "center",
            gap: "5px",
            width: "fit-content",
            userSelect: "none"
          }}>
            <Typography sx={{          
              lineHeight: "35px",
              fontSize: "122%",
              color: "#C3C9D9",
              marginRight: "15px",
            }}>Tempo: </Typography>
          <TransportInput
            value={bpm} 
            onChange={(e) => {  
              var value = Number(e.target.value);
              setBpm(value || 40);

            }}
          />
            <Typography sx={{          
              lineHeight: "35px",
              fontSize: "122%",
              color: "#C3C9D9",
              marginRight: "15px",
            }}>BPM</Typography>
          </Box>        
          <Box sx={{
            marginRight: "30px",
            marginLeft: "15px",
            display: "flex",
            flexDirection: "row",
            alignItems: "center",
            gap: "5px",
            width: "fit-content",
            userSelect: "none"
          }}>
            <Typography sx={{          
              lineHeight: "35px",
              fontSize: "122%",
              color: "#C3C9D9",
              marginRight: "15px",
            }}>Time Signature: </Typography>
          <SignatureInput
            value={timeSig.numerator} 
            onChange={(e) => {  
              var value = Number(e.target.value);
              setTimeSignature(value || 4, timeSig.denominator);
            }}
          />
            <Typography sx={{          
              lineHeight: "35px",
              fontSize: "122%",
              color: "#C3C9D9",
            }}>/</Typography>
            <SignatureInput
              value={timeSig.denominator} 
              onChange={(e) => {  
                var value = Number(e.target.value) as (1|2|4|8|16);
                setTimeSignature(timeSig.numerator, value || 4);
              }}
            />
          </Box>
          <Box sx={{
            marginRight: "30px",
            marginLeft: "15px",
            display: "flex",
            flexDirection: "row",
            alignItems: "center",
            gap: "5px",
            width: "fit-content",
            userSelect: "none"
          }}>
            <Typography sx={{          
              lineHeight: "35px",
              fontSize: "122%",
              color: "#C3C9D9",
            }}>Measures: </Typography>
            <SignatureInput value={measureCountInput} onChange={(e) => onChangeMeasures(Number(e.target.value) || 1)} />
            
          </Box>        
        </TransportSection>
        <TrackManagerSection>
        <Box display="flex" flexDirection="row" gap="15px">

        <TrackManagerButton onClick={showAddTrack}>
          <Box display="flex" flexDirection="row" alignContent="center" alignItems="center" justifyContent="center">
            <AddIcon sx={{fontWeight: "900", fontSize: "220%"}} /><Typography>Add Track</Typography>
          </Box>
        </TrackManagerButton>

        <TrackManagerButton onClick={() => {
          rebuildGrid(timeSig.numerator, subdivisionsPerBeat)
        }}>
          <Box display="flex" flexDirection="row" alignContent="center" alignItems="center" justifyContent="center">
            <DeleteOutlineIcon sx={{fontWeight: "900", fontSize: "220%"}} /><Typography>Clear Pattern</Typography>
          </Box>
        </TrackManagerButton>        

        </Box>
        </TrackManagerSection>
        <SequenceSection sx={sequenceSx}>
          {sequence?.tracks && sequence.tracks.map((track, trackIndex) => 
            (
            <TrackSection key={trackIndex} sx={{width: "fit-content"}} display="flex" flexDirection="row">
              <ControlsSection display="flex" flexDirection="row" alignItems="center" justifyContent="space-between" sx={{backgroundColor: track.channelEnabled ? "#282B32" : "#2B2B2B"}}>
                <Box display="flex" flexDirection="row" alignItems="center" gap={.5} alignContent={"center"} position={"relative"}>
                  <span 
                  onClick={() => setTrackEnabled(trackIndex, !track.channelEnabled)}
                  style={{
                    userSelect: "none",
                    display: "block",
                    position: "absolute",
                    width: "10px",
                    height: "10px",
                    borderRadius: "50%",
                    cursor: "pointer",
                    backgroundColor: track.channelEnabled ? "#fff" : "#111",
                    boxShadow: track.channelEnabled ? "0 0 8px rgba(195, 201, 217, .7)" : "none",
                    top: "10px",
                    left: "10px",
                    zIndex: 3                    
                  }}></span>
                  <ImageKnob 
                    disabled={!track.channelEnabled} 
                    src="/controls/BOS_knob_STGA_30X30_128f.png" 
                    frames={128} 
                    diameter={30} 
                    value={track.channelVolume} 
                    min={0} 
                    max={10} 
                    step={.1} 
                    onChange={(v) => { setTrackVolume(trackIndex, v)} }  />  
                  <Box ml={1} >
                    {track.channelEditing /* todo check for proper state here */ ?
                      <input
                        autoFocus
                        type="text"
                        style={{
                          backgroundColor: "transparent",
                          width: "100px",
                          fontSize: "90%",
                          color: track.channelEnabled ? "#FFF" : "#666",
                          border: "none",
                          outline: "none",
                          borderBottom: "dotted 2px rgba(195, 201, 217, .5)",
                          boxShadow: "none",
                        }}
                        value={track.channelName}
                        onChange={(e) => setChannelNameAt(trackIndex, e.target.value)}
                        onBlur={() => setChannelEditing(trackIndex, false)}
                        onKeyDown={(e) => { if (e.key === "Enter") { setChannelEditing(trackIndex, false); } }}
                      />
                      :
                      <Typography 
                        onClick={() => {
                          console.log("click");
                          track.channelEnabled && setChannelEditing(trackIndex, true)}
                        }
                        style={{
                          textOverflow: "ellipsis",
                          overflow: "hidden",
                          width: "100px",
                          lineHeight: "1.2",
                          color: track.channelEnabled ? "#FFF" : "#666",
                          userSelect: "none"}}>{track.channelName}</Typography>
                    }
                  </Box>
                </Box>
                <Box display="flex" flexDirection="row" alignItems="center" gap={.5} alignContent={"center"}>
                  <Box>
                  <ControlButton>
                    <SwapHorizIcon style={{color: "#eee", padding: "0"}} />
                  </ControlButton>
                  </Box>
                  <Box>
                  <ControlButton onClick={() => removeTrack(track.id)}>
                    <ClearIcon style={{color: "#eee", padding: "0"}} />
                  </ControlButton>
                  </Box>
                  <Box sx={{
                    userSelect: "none",
                    width: "7px",
                    height: "25px",
                    backgroundColor: track.triggered ? "#FFF" : "#111",
                    boxShadow: track.triggered ? "0 0 5px rgba(195,201,217,.7)" : "none",
                    transition: "background-color 120ms linear, box-shadow 120ms linear",                    
                    borderRadius: "4px",
                    margin: "4px 5px 5px 9px",
                    opacity: track.channelEnabled ? 1 : 0.4,
                    }}>
                  </Box>              
                </Box>
              </ControlsSection>
              <PatternSection sx={{width: "fit-content"}} display="flex" flexDirection="row" gap="0">
                {track.pattern.measures && track.pattern.measures.map((measure, measureIndex) =>
                (                
                <MeasureSection display="flex" flexDirection="row" key={measureIndex}>
                    {measure.beats && measure.beats.map((beat, beatIndex) =>(
                      <BeatSection display="flex" flexDirection="row" key={beatIndex}>
                        {beat.steps && beat.steps.map((step, stepIndex) => {
                        const isNow = (isPlaying || (playheadRef.current.paused ?? false)) &&
                          playheadRef.current.measure === measureIndex &&
                          playheadRef.current.beat === beatIndex &&
                          playheadRef.current.step === stepIndex;                          

                          return (<StepCell 
                            key={stepIndex} 
                            evenBeat={beatIndex % 2 === 1}
                            active={step.active} 
                            isNow={isNow} 
                            disabled={!track.channelEnabled}
                            onClick={() => toggleStep(trackIndex, measureIndex, beatIndex, stepIndex)} 
                            ></StepCell>);
                        }
                        )}
                      </BeatSection> 
                    ))}
                </MeasureSection>)
                )}
              </PatternSection>
            </TrackSection>
            )
          )}
        </SequenceSection>
      </SequencerSection>
    </MainSection>
    </>
  )
}

export default App
